# Combien d'handle contiennent les 4 lettres du prénom "Aude" à la suite (sans prendre en compte les majuscules) ? 
def content_aude (journalists)

    i = 0
    journalists.each do |b_handle|
        check_handle = b_handle.downcase
    if check_handle.include?("aude")
        i += 1
    end
    end

    puts i.to_s + " aude à la vie,c'est déjà ça!!!"
 
end


# Combien commencent par une majuscule (première lettre juste après le @) ? 


def start_maj(journalists)

    i = 0
    journalists.each do |c_handle|
        split_handle = c_handle.to_s[1]
    if split_handle.downcase!
        i += 1
    end

    end

    puts i.to_s + " , Attention Mesdames,Messieurs,la taille ne fait pas tout!!!"
end



